// Processar dados recebidos para visualiza√ß√£o
const data = msg.payload.data;

// Formatar dados para o dashboard
msg.payload = {
    // Dados principais
    id: data.id || Date.now(),
    nome: data.nome || 'Sem nome',
    valor: parseFloat(data.valor) || 0,
    categoria: data.categoria || 'Geral',

    // Metadados de processamento
    recebido_em: new Date().toISOString(),
    origem: 'API_JAVA',
    status: 'PROCESSADO',

    // Dados adicionais (se existirem)
    fornecedor: data.fornecedor || 'N/A',
    estoque: data.estoque || 0,
    descricao: data.descricao || '',

    // Timestamp de processamento
    processado_em: new Date().toISOString()
};

// Adicionar dados para estat√≠sticas
msg.payload.valor_formatado = `R$ ${msg.payload.valor.toFixed(2)}`;
msg.payload.timestamp_legivel = new Date().toLocaleString('pt-BR');

return msg;

// Preparar dados para o dashboard principal
const data = msg.payload;

// Enviar para diferentes widgets do dashboard
msg.payload = {
    // Widget de dados recebidos
    dados_recentes: {
        id: data.id,
        nome: data.nome,
        valor: data.valor_formatado,
        categoria: data.categoria,
        recebido_em: data.timestamp_legivel
    },

    // Widget de status
    status: {
        ultima_atualizacao: data.timestamp_legivel,
        status: data.status,
        origem: data.origem
    },

    // Widget de valores
    valores: {
        valor_atual: data.valor,
        valor_formatado: data.valor_formatado,
        categoria: data.categoria
    }
};

return msg;

// Atualizar contadores e estat√≠sticas
const data = msg.payload;

// Incrementar contadores globais
if (!global.contadores) {
    global.contadores = {
        total_recebidos: 0,
        total_valor: 0,
        categorias: {},
        ultima_atualizacao: null
    };
}

// Atualizar contadores
global.contadores.total_recebidos++;
global.contadores.total_valor += data.valor;
global.contadores.ultima_atualizacao = data.timestamp_legivel;

// Contar por categoria
if (!global.contadores.categorias[data.categoria]) {
    global.contadores.categorias[data.categoria] = 0;
}
global.contadores.categorias[data.categoria]++;

// Preparar dados para widgets de estat√≠sticas
msg.payload = {
    total_recebidos: global.contadores.total_recebidos,
    total_valor: `R$ ${global.contadores.total_valor.toFixed(2)}`,
    valor_medio: `R$ ${(global.contadores.total_valor / global.contadores.total_recebidos).toFixed(2)}`,
    ultima_atualizacao: global.contadores.ultima_atualizacao,
    categorias: global.contadores.categorias
};

return msg;

// Log detalhado dos dados recebidos
const data = msg.payload;

// Log para debug
node.log(`üì• Dados recebidos da API Java:`);
node.log(`   ID: ${data.id}`);
node.log(`   Nome: ${data.nome}`);
node.log(`   Valor: ${data.valor_formatado}`);
node.log(`   Categoria: ${data.categoria}`);
node.log(`   Recebido em: ${data.timestamp_legivel}`);

// Preparar para exibi√ß√£o no log
msg.payload = {
    timestamp: data.timestamp_legivel,
    id: data.id,
    nome: data.nome,
    valor: data.valor_formatado,
    categoria: data.categoria,
    origem: data.origem
};

return msg;

// Atualizar dashboard a cada 10 segundos
if (global.contadores) {
    msg.payload = {
        ultima_atualizacao: new Date().toLocaleString('pt-BR'),
        status: 'ATIVO',
        contadores: global.contadores
    };
} else {
    msg.payload = {
        ultima_atualizacao: new Date().toLocaleString('pt-BR'),
        status: 'AGUARDANDO DADOS',
        contadores: {
            total_recebidos: 0,
            total_valor: 'R$ 0,00'
        }
    };
}

return msg;
